import { userState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { register, reset } from "../features/auth/authSlice";

function Register() {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [data, setData] = userState({
    username: "",
    email: "",
    password: "",
    password2: "",
  });

  const { username, email, password, password2 } = data;

  const { user, isLoading, isError, isSuccess, message } = useSelector(
    (state) => state.auth
  );

  useEffect(() => {
    if (isError) {
      toast.error(message);
    }

    if (isSuccess || user) {
      navigate("/login");
    }

    dispatch(reset());
  }, [user, isError, isSuccess, message, navigate, dispatch]);

  if(isLoading) {
    return <Spinner/>
  }

  return (
    <div className="register">
      <h1 className="text-white text-center text-3xl m-8">Register</h1>
      <form>
        <div className="grid grid-cols-1 gap-5 my-16 mx-12">
          <input type="text" placeholder="username" />
          <input type="email" placeholder="E-mail" />
          <input type="password" placeholder="password" />
          <input type="password" placeholder="confirm password" />
          <input type="submit" className="my-20" value="register" />
          <h5 className="login-link text-white text-sm">
            Already have an account ?{" "}
            <Link to="/login" className="link text-lg">
              login
            </Link>
          </h5>
        </div>
      </form>
    </div>
  );
}

export default Register;
